import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import { Button, Container } from "@material-ui/core";
import { useState } from "react";
import api from "../Api";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  title: {
    textAlign: "center",
    marginTop: "90px",
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: 230,
    },
  },
  form: {
    paddingLeft: 100,
    margin: 0,
  },
}));

const CadProdutos = () => {
  const classes = useStyles();

  const [id, setId] = useState("");
  const [nome, setNome] = useState("");
  const [preco, setPreco] = useState("");
  const [quantidade, setQuantidade] = useState("");
  const [error, setError] = useState("");
  const [isNew, setIsNew] = useState(true);
  const location = useLocation();

  useEffect(() => {
    const { state } = location;
    const { dados, create } = state;
    setIsNew(create);

    if (dados) {
      setNome(dados.nome);
      setPreco(dados.preco);
      setQuantidade(dados.quantidade);
      setId(dados.id);
    } else {
      setNome("");
      setPreco("");
      setQuantidade("");
    }
  }, []);

  const handleSubmit = async (e) => {
    console.log("aqui", isNew);
    const json = await isNew
      ? api.CadProdutos(id, nome, preco, quantidade)
      : api.PutProdutos(id, nome, preco, quantidade);
    console.log("nome", nome);
    if (json.error) {
      setError(json.error);
    } else {
      window.location.href = "/produtos";
    }
  };

  return (
    <>
      <Container>
        <h1 className={classes.title}>
          {isNew ? "Cadastrar" : "Editar"} Produtos
        </h1>
        <Grid className={classes.form}>
          <form
            className={classes.title}
            noValidate
            autoComplete="off"
            onSubmit={handleSubmit}
          >
            <Grid container>
              <Grid item xs={12}>
                <TextField
                  type="text"
                  label="Nome"
                  onChange={(e) => setNome(e.target.value)}
                />
                <TextField
                  label="Preco"
                  onChange={(e) => setPreco(e.target.value)}
                />
                <TextField
                  label="Quantidade"
                  onChange={(e) => {
                    console.log("Qian", e.target.value);

                    setQuantidade(e.target.value);
                  }}
                />
              </Grid>
            </Grid>

            <Button variant="contained" color="primary" onClick={handleSubmit}>
              {" "}
              Cadastrar Produtos
            </Button>
          </form>
        </Grid>
      </Container>
    </>
  );
};

export default CadProdutos;
